mapper.select:
SELECT 

FROM project_apply_external a

WHERE a.id = #{id}

SELECT

FROM project_apply_external a

WHERE a.proc_ins_id = #{procInsId}

SELECT 

FROM project_apply_external a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.project_code = #{projectCode}


		AND a.project_name LIKE 
			'%'||#{projectName}||'%'
			'%'+#{projectName}+'%'
			concat('%',#{projectName},'%')


		AND a.saler_id LIKE 
			'%'||#{saler.id}||'%'
			'%'+#{saler.id}+'%'
			concat('%',#{saler.id},'%')


		AND a.customer_id LIKE 
			'%'||#{customer.id}||'%'
			'%'+#{customer.id}+'%'
			concat('%',#{customer.id},'%')


		AND a.category = #{category}


		AND a.process_status = #{procStatus}


		AND a.pro_main_stage = #{proMainStage}



	    AND a.pro_main_stage in 

	        #{item}





		AND a.pro_stage = #{proStage}

		${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM project_apply_external a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.pro_main_stage  >= #{proMainStage} 

	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM project_apply_external a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE project_apply_external SET project_code = #{projectCode}, project_name = #{projectName}, customer_id = #{customer.id}, customer_contact_id = #{customerContact.id}, estimated_contract_amount = #{estimatedContractAmount}, estimated_gross_profit_margin = #{estimatedGrossProfitMargin}, estimated_time_of_signing = #{estimatedTimeOfSigning}, category = #{category}, description = #{description}, estimated_gross_profit_margin_description = #{estimatedGrossProfitMarginDescription}, risk_analysis = #{riskAnalysis}, document_attachment_path = #{documentAttachmentPath}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, ownership = #{ownership}, pro_main_stage = #{proMainStage}, pro_stage = #{proStage}, proc_ins_id = #{procInsId}, process_status = #{procStatus},
apply_flag = #{applyFlag}, bidding_flag = #{biddingFlag}, contract_flag = #{contractFlag}, finish_flag = #{finishFlag},
outsourcing = #{outsourcing},
docPath = #{docPath}
WHERE id = #{id}

UPDATE project_apply_external SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
