mapper.select:
SELECT 

FROM customer_invoice a

WHERE a.id = #{id}

SELECT 

FROM customer_invoice a



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.customer_name LIKE 
			'%'||#{customerName}||'%'
			'%'+#{customerName}+'%'
			concat('%',#{customerName},'%')


		AND a.industry = #{industry}


		and(
		 1 = 1

		 ${sqlMap.dsf}

		 or u4.id is null
		)





		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM customer_invoice a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE customer_invoice SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT
FROM customer_invoice a
WHERE a.customer_name = #{customerName} AND a.del_flag = #{DEL_FLAG_NORMAL}
