mapper.select:
SELECT 

FROM project_bidding_archive a

WHERE a.id = #{id}

SELECT

FROM project_bidding_archive a

WHERE a.proc_ins_id = #{procInsId}

SELECT 

FROM project_bidding_archive a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND pro_apply.project_code LIKE 
			'%'||#{apply.projectCode}||'%'
			'%'+#{apply.projectCode}+'%'
			concat('%',#{apply.projectCode},'%')


		AND pro_apply.project_name LIKE
		'%'||#{apply.projectName}||'%'
		'%'+#{apply.projectName}+'%'
		concat('%',#{apply.projectName},'%')


		AND a.category = #{category}


		AND a.printing_paste = #{printingPaste}

	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM project_bidding_archive a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO project_bidding_archive( apply_id, tenderer, category, price, printing_paste, content, amount, gross_margin, profit_margin, result, id, create_by, create_date, update_by, update_date, remarks, del_flag, proc_ins_id, process_status, profit_margin_file, outsourcing,
issue_date, bidding_date, open_date, bidding_members, tenderee, tenderee_info, judge_info, co_bidder_info, prepare, bidding_price, bidding_info, final_price, final_info, bidding_issue, win_flag, lost_info, archive_flag, alter_flag
) VALUES ( #{apply.id}, #{tenderer}, #{category}, #{price}, #{printingPaste}, #{content}, #{amount}, #{grossMargin}, #{profitMargin}, #{result}, #{id}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{remarks}, #{delFlag}, #{procInsId}, #{procStatus}, #{profitMarginFile}, #{outsourcing},
#{issueDate}, #{biddingDate}, #{openDate}, #{biddingMembers}, #{tenderee}, #{tendereeInfo}, #{judgeInfo}, #{cobidderInfo}, #{prepare}, #{biddingPrice}, #{biddingInfo}, #{finalPrice}, #{finalInfo}, #{biddingIssue}, #{winFlag}, #{lostInfo}, #{archiveFlag}, #{alterFlag}
)

mapper.update:
UPDATE project_bidding_archive SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
