mapper.select:
SELECT 

FROM project_contract a

WHERE a.id = #{id}

SELECT

FROM project_contract a

WHERE a.contract_code = #{contractCode} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT

FROM project_contract a

WHERE a.proc_ins_id = #{procInsId}

SELECT 

FROM project_contract a

WHERE pro_apply.id = #{prjId}

SELECT
FROM project_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.create_date BETWEEN #{queryBeginDate} AND #{queryEndDate}
AND a.create_date <= #{queryEndDate}
AND a.create_date >= #{queryBeginDate}
AND pro_apply.project_code LIKE '%'||#{apply.projectCode}||'%' '%'+#{apply.projectCode}+'%' concat('%',#{apply.projectCode},'%')
AND pro_apply.project_name LIKE '%'||#{apply.projectName}||'%' '%'+#{apply.projectName}+'%' concat('%',#{apply.projectName},'%')
AND pro_apply.category LIKE '%'||#{apply.category}||'%' '%'+#{apply.category}+'%' concat('%',#{apply.category},'%')
AND a.contract_type = #{contractType}
AND a.client_name LIKE '%'||#{clientName}||'%' '%'+#{clientName}+'%' concat('%',#{clientName},'%')
AND a.contract_code LIKE '%'||#{contractCode}||'%' '%'+#{contractCode}+'%' concat('%',#{contractCode},'%')
AND a.proc_status = #{procStatus}
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM project_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

select
from project_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
and date_sub(curdate(), INTERVAL 60 DAY) <= date(a.end_date) and date_sub(curdate(), INTERVAL 30 DAY) >= date(a.end_date)
ORDER BY ${page.orderBy}
ORDER BY a.end_date DESC

select
from project_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
and date_sub(curdate(), INTERVAL 60 DAY) <= date(a.end_date) and date_sub(curdate(), INTERVAL 30 DAY) >= date(a.end_date)
ORDER BY ${page.orderBy}
ORDER BY a.end_date DESC

select
from project_contract a
a.del_flag = #{DEL_FLAG_NORMAL}
and date_sub(curdate(), INTERVAL 30 DAY) <= date(a.end_date) and date_sub(curdate(), INTERVAL 0 DAY) >= date(a.end_date)
ORDER BY ${page.orderBy}
ORDER BY a.end_date DESC

select

from project_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}


		and date_sub(curdate(), INTERVAL 0 DAY) >= date(a.end_date)
		and a.handle_flag = #{DEL_FLAG_NORMAL}
		and pro_apply.end_flag = #{DEL_FLAG_NORMAL}





		ORDER BY ${page.orderBy}


		ORDER BY a.end_date DESC

select
	count(1)
from project_contract a

	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.handle_flag = #{DEL_FLAG_NORMAL}

		and date_sub(curdate(), INTERVAL 60 DAY) <= date(end_date)

mapper.update:
UPDATE project_contract SET project_id = #{apply.id}, project_manager_id = #{projectManager.id}, stage = #{stage},
contract_name = #{contractName}, contract_code = #{contractCode}, client_name = #{clientName}, amount = #{amount}, amount_detail = #{amountDetail}, content_summary = #{contentSummary}, begin_date = #{beginDate}, end_date = #{endDate}, valid_info = #{validInfo}, resign_flag = #{resignFlag}, resign_info = #{resignInfo}, payment_type = #{paymentType}, purchase_code = #{purchaseCode}, supplier_name = #{supplierName}, profit_margin = #{profitMargin}, delivery_date = #{deliveryDate}, delivery_address = #{deliveryAddress}, oa_no = #{oaNo}, seal_type = #{sealType}, contract_type = #{contractType}, origin_code = #{originCode}, audit_end_date = #{auditEndDate},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, attachment = #{attachment}, proc_ins_id = #{procInsId}, proc_status = #{procStatus} WHERE id = #{id}

UPDATE project_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE project_contract SET
handle_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
