contract_code = #{contractCode}, client_name = #{clientName}, amount = #{amount}, amount_detail = #{amountDetail}, content_summary = #{contentSummary}, begin_date = #{beginDate}, end_date = #{endDate}, resign_flag = #{resignFlag}, resign_info = #{resignInfo}, payment_type = #{paymentType}, purchase_code = #{purchaseCode}, supplier_name = #{supplierName}, profit_margin = #{profitMargin}, delivery_date = #{deliveryDate}, delivery_address = #{deliveryAddress}, oa_no = #{oaNo}, seal_type = #{sealType}, contract_type = #{contractType}, origin_code = #{originCode},
update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, attachment = #{attachment}, proc_ins_id = #{procInsId}, proc_status = #{procStatus} WHERE id = #{id}

