mapper.insert:
INSERT INTO project_finish_approval( project_id, category, risk_assessment, project_accounting, project_accounting_file, id, create_by, create_date, update_by, update_date, remarks, del_flag, proc_ins_id, proc_status,
estimated_profit_margin, settlement_profit_margin, profit_margin_float, profit_margin_info, fee_info, running_plan, summary, achievement, finish_date, finish_price ) VALUES ( #{apply.id}, #{category}, #{riskAssessment}, #{projectAccounting}, #{projectAccountingFile}, #{id}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{remarks}, #{delFlag}, #{procInsId}, #{procStatus},
#{estimatedProfitMargin}, #{settlementProfitMargin}, #{profitMarginFloat}, #{profitMarginInfo}, #{feeInfo}, #{runningPlan}, #{summary}, #{achievement}, #{finishDate}, #{finishPrice}
)

mapper.update:
UPDATE project_finish_approval SET project_id = #{apply.id}, category = #{category}, risk_assessment = #{riskAssessment}, project_accounting = #{projectAccounting}, project_accounting_file =#{projectAccountingFile}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, proc_ins_id = #{procInsId}, proc_status = #{procStatus},
estimated_profit_margin = #{estimatedProfitMargin}, settlement_profit_margin = #{settlementProfitMargin}, profit_margin_float = #{profitMarginFloat}, profit_margin_info = #{profitMarginInfo}, fee_info = #{feeInfo}, running_plan = #{runningPlan}, summary = #{summary}, achievement = #{achievement}, finish_date = #{finishDate}, finish_price = #{finishPrice}
WHERE id = #{id}

UPDATE project_finish_approval SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
